function [hms] = EnHMS (segundos)  H= segundos/3600; %calculamos las horas  h= floor(H); %cogemos el valor entero, es decir, las horas  RestoH=(H-h)*3600; %con el decimal restante, calculamos los segundos que sobran    if RestoH>=60 %con el if definimos si hay minutos o no  M= RestoH/60; %calculamos los minutos  m=floor(M); %cogemos el valor entero, es decir, los minutos  RestoM=(M-m)*60; %con el decimal restante, calculamos los segundos que sobran  s=floor(RestoM); %obtenemos los segundos sin decimales  else %en caso de que no hallan minutos  m=0;  s=floor(RestoH); %calculamos los segundos de forma entera  end  hms=sprintf('%.2d:%.2d:%.2d',h,m,s); %guardamos los datos como valor de salida y en el formato solicitadoend