for ruta= {'n1','a1'} for i=1:2     f=sprintf ('%s-driver%d-log.csv', ruta{1},i);    %disp(f);    M=dlmread(f,',',1,0);    %Separamos cada columna del fichero    KM=M(:,1);    KMH=M(:,2);   %hasta que no arreglemos la funcion estimateTime no tendremos valores correctos    H=estimateTime(KM,KMH,1000);       tiempohoras=H*3600;       hor1=tiempohoras/3600;   %la funcion fix quita los decimales sin redondeo   hor=fix (H);    a=rem(tiempohoras,3600);    minuto1=a/60;    minuto=fix (minuto1);    b=rem(a,60);    seg=fix(b);   %fprintf('\n%02d:%02d:%02d\n',hor,minuto,seg)    fprintf ('Estimated time for driver%d in route %s: %02d:%02d:%02d\n',i,ruta{1},hor,minuto,seg)       endend