for ruta= {'n1','a1'} for i=1:2     f=sprintf ('%s-driver%d-log.csv', ruta{1},i);    disp(f);    M=dlmread(f,',',1,0);    %Separamos cada columna del fichero    KM=M(:,1);    KMH=M(:,2);       Media_Velocidad=mean (KMH); %Hallamos la altura media (funcion mean )     Desvi=std(KMH); %Desviacion estandar se denomina con la funcion ( std  )    %calcular máximos y mínimos    Max=max(KMH);    Min=min(KMH);    fprintf ('Estadísticas del conductor %d en la ruta %s:\n',i,ruta{1})    fprintf ('Velocidad media: %0.2f (sd: %0.2f)\n',Media_Velocidad,Desvi)    fprintf ('Rango de velocidades: [%0.2f , %0.2f]\n\n',Min,Max)    %gráficas (ejex DISTANCIA,ejey ALTURA) para N y A          subplot(1,2,i);      hold on;      title('VELOCIDAD-DISTANCIA AL ORIGEN CONDUCTOR');      xlabel ('DISTANCIA AL ORIGEN (KM)');      ylabel ('VELOCIDAD (KM/H)');      plot(KM,KMH);               %graficas (ejex LONGITUD,ejey LATITUD)  N y A         endendsaveas(gcf,'route-speed.png');