%creamos un for para que nos lea los archivosfor ruta={'n1','a1'}  f=sprintf ('%s-height.csv',ruta{1});  M=dlmread(f,',',1,0);  %asignamos a cada columna lo que corresponde  LAT=M(:,1);  LONG=M(:,2);  ELEV=M(:,3);  DIST=M(:,4);  GRAD=M(:,5);  %Hallamos la altura media (funcion mean )  Media_altura=mean (ELEV);  %Desviacion estandar se denomina con la funcion ( std  )  DESV=std(ELEV);  %calcular máximos y mínimos  MAX=max(ELEV);  MIN=min(ELEV);  fprintf ('Estadísticas de la ruta %s:\n',ruta{1})  fprintf ('Altura media: %0.2f (sd: %0.2f)\n',Media_altura,DESV)  fprintf ('Rango de alturas: [%0.2f , %0.2f]\n\n',MIN,MAX)   %gráficas (ejex distancia,ejey altura) para N y A  subplot(1,2,1);  hold on;  title('ALTURA-DISTANCIA AL ORIGEN');  xlabel ('DISTANCIA AL ORIGEN (KM)');  ylabel ('ALTURA (M)');  plot(DIST,ELEV);  subplot(1,2,2);  hold on;  %graficas (ejex LONGITUD,ejey LATITUD)  N y A  title('LONGITUD-LATITUD');  xlabel ('LONGITUD (KM)');  ylabel ('LATITUD (KM)');  plot(LONG,LAT);end%para guardar la graficasaveas (gcf,'route-elevations.png');