function [kmsAboveSpeedLimit, percentAboveSpeddLimit]= checkSpeedLimits(diverLogKm,driverLogSpeed,limitSpeeds,numSlices)for ruta= {'n1','a1'}    p=sprintf ('%s-speed-limit.csv', ruta{1});    disp(p)    M=dlmread(p,';');    TRAMO=M(:,1);    LIMITEVEL=M(:,2); for i=1:2     f=sprintf ('%s-driver%d-log.csv', ruta{1},i);    disp(f)    M=dlmread(f,',',1,0);    KM=M(:,1);    KMH=M(:,2);    longitud=length(KM);    k=1;    for j=1:longitud      if KM(j)> Tramo(1)&& KM(j)<TRAMO(2)          if KMH(j)> LIMITEVEL(1)              exceskm(k)=KM(j)              excesovel(k)=KMH(j)           end         else              if KM(j)>=TRAMO(2) && KM(j)<TRAMO(3)            if KMH(j)> LIMITEVEL(2)               excesokm(k)=KM(j)              excesovel(k)=KMH(j)            end                    else KM(j)>=TRAMO(3)          if KMH(j)> LIMITEVEL(3)            excesokm(k)=KM(j)            excesovel(k)=KMH(j)            end        end      k=k+1;          end              end      end    end