function [ estimatedTime ] = estimateTime( kms, speedKmH, numSlices)%calculamos la última posición por si kms tiene más de 4 valores f=length(kms);r=length(speedKmH);Kmintervalo=linspace(kms(1),kms(f),1000);e=(f-kms(1))/numSlices; tt=0;limitefor=numSlices-1;%en el for no podemos coger el valor maximo de slices ya que nos daría error al calcular el espacio  for i:limitefor;     v=interpolateLinearly(kms,speedKmH,kms(Kmintervalo));      tt=tt+(e/v);       endestimateTime=tt;end