function [ interpolatedY ] = interpolateToTheLeft( xVector, yVector , x)  for ruta= {'n1','a1'}    f=sprintf ('%s-speed-limit.csv', ruta{1});    disp(f)    M=dlmread(f,';');        TRAMO=M(:,1);    LIMITEVEL=M(:,2);    if x>=TRAMO(1) && x<TRAMO(2)       y=LIMITEVEL(1)    else        if x>=TRAMO(2) && x<TRAMO(3)          y=LIMITEVEL(2)        else x>=TRAMO(3)          y=LIMITEVEL(3)        end    end          interpolatedY=y;              endend